import React from 'react'

import axios from 'axios'

import {
    GET_PROFILE,
    PROFILE_ERROR
} from './actions'


export const getCurrentPofile = () => async dispatch =>  {
    try {
        const res = await axios.get('/api/profile/me')       
    
        dispatch({
            type:GET_PROFILE,
            payload:res.data
        })
    
    } catch (err) {
        dispatch({
            type:PROFILE_ERROR,
            payload:{msg: err.response.statusText,status:err.response.status}
        })        
    }

}
