import { PROFILE_ERROR,GET_PROFILE,CLEAR_PROFILE ,UPDATE_PROFILE} from "../actions.js/actions"

const initialState = {
    profile:null,
    profiles:[],
    repos:[],
    loading:true,
    error:{}
}


export default function (state=initialState,action){
    const {type,payload} = action
    console.log(payload)
    switch (type) {
        case GET_PROFILE:
        case UPDATE_PROFILE:
            return {
                ...state,
                profile:payload,
                loading:false
            }

        case GET_PROFILES:
                return{
                    ...state,
                    profiles:payload,
                    loading:false
                }
        
        case GET_REPOS:
            return{
                ...state,
                repos:payload,
                loading:false

            }

        case PROFILE_ERROR:
            return{
                ...state,
                error:payload,
                loading:false
            }
        case CLEAR_PROFILE:
            return{
                ...state,
                profile:null,
                repos:[]
            }
        

        default:
           return state
    }

}
