import React from 'react'
import {
    GET_POST,
    POST_ERROR,
    UPDATE_LIKES,
    DELETE_POST
} from '../actions.js/actions'

const initialState = {
    posts:[],
    post:null,
    loading:true,
    error:{}
}

export default function (state=initialState,action) {
    const {type,payload} = action

    switch (type) {
        case GET_POST:
            return{
                ...state,
                posts:payload,
                loading:false
            }
        case UPDATE_LIKES:
            console.log(payload)
            return{
                ...state,
                posts:state.posts.map(post => post._id === payload.id ? {...post,likes:payload.likes} :post),
                loadig:false
            }
        
        case POST_ERROR:
            return{
                ...state,
                error:payload,
                loading:false
            }
    
        default:
            return{
                ...state,
                loading:false
            }
            
    }
}
