import React, { Fragment, useEffect } from 'react'
import {Link} from 'react-router-dom'
import {connect} from 'react-redux'
import {getProfileById} from '../../actions.js/profile'

const Profile = ({
    getProfileById,
    profile:{profile,loading},
    auth,
    match   
}) => {

    useEffect(()=>{
        getProfileById(match.params.id)
    },[getProfileById])
 
    return <Fragment>
        {profile === null || loading ? <div>Loading</div> : <Fragment>
            <Link to='/profiles' className ='btn btn-light'>Bact to Profiles</Link>
            {auth.isAuthenticated && auth.loading === false && auth.user.id === match.params.id &&
            (<Link to='edit-experience' className='btn btn-dark'> Edit Profile</Link>)}     
            </Fragment>}
    </Fragment>
}


const mapStateToProps = (state) => ({
    profile:state.profile,
    auth:state.auth
})

export default connect(mapStateToProps,{getProfileById})(Profile)
