import { PROFILE_ERROR,GET_PROFILE } from "../actions.js/actions"

const initialState = {
    profile:null,
    profiles:[],
    repos:[],
    loading:true,
    error:{}
}


export default function (state=initialState,action){
    const {type,payload} = action
    console.log(payload)
    switch (type) {
        case GET_PROFILE:
            return {
                ...state,
                profile:payload.data,
                loading:false
            }

        case PROFILE_ERROR:
            return{
                ...state,
                error:payload,
                loading:false
            }
            
        default:
           return state
    }

}
