import React, { Fragment, useEffect } from 'react'
import {Link} from 'react-router-dom'
import {connect} from 'react-redux'
import {getProfileById} from '../../actions.js/profile'
import ProfileTop from './ProfileTop'
import ProfileAbout from './ProfileAbout'


const Profile = ({
    getProfileById,
    profile:{profile,loading},
    auth,
    match   
}) => {

    useEffect(()=>{
        getProfileById(match.params.id)
    },[getProfileById])
    
    console.log(auth)
    return <Fragment>
        {profile === null || loading ? <div>Loading</div> : <Fragment>
            <Link to='/profiles' className ='btn btn-light'>Bact to Profiles</Link>
            {auth.isAuthenticated && auth.loading === false && auth.user._id === match.params.id &&
            (<Link to='edit-experience' className='btn btn-dark'> Edit Profile</Link>)}     
            <div className='profile-grid my-1' >
             <ProfileTop profile={profile} />         
             <ProfileAbout profile={profile} />
             <div className='profile-exp bg-white p-2'>
                 {
                     profile.experience > 0 ?
                      (<Fragment>
                        <h2 className='text-primary' >Experience</h2>
                          {
                    
                        profile.experience.map(exp => <ProfileExperience key={exp.id} experience = {exp} /> )

                        }</Fragment>) :
                          (<h4>No Experience Credentials</h4>)
                 }

             </div>
            </div>
            </Fragment>}

    </Fragment>
}


const mapStateToProps = (state) => ({
    profile:state.profile,
    auth:state.auth
})

export default connect(mapStateToProps,{getProfileById})(Profile)
