import * as uuid from 'uuid'

import {SET_ALERT,REMOVE_ALERT} from './actions'

export const setAlert = (msg,alertType) => dispatch => {
    const id = uuid.v4()
    
    dispatch({
        type:SET_ALERT,
        payload:{msg,alertType,id}
    })

    setTimeout(()=>{dispatch({type:REMOVE_ALERT,payload:id})},3000)
};
